<?php

function watermark($data)
{
    $credentials = new \Aws\Credentials\Credentials($_ENV['public_key'], $_ENV['secret_key']);

    $s3 = new \Aws\S3\S3Client([
        'region'  => 'eu-central-1',
        'version' => 'latest',
        'credentials' => $credentials
    ]);

    if (!isset($data['Records'])) {
        return "Not a S3 Event";
    }

    $key = $data['Records'][0]['s3']['object']['key'];
    $path = explode('/', $key);

    $result = $s3->getObject([
        'Bucket' => $data['Records'][0]['s3']['bucket']['name'],
        'Key' => $data['Records'][0]['s3']['object']['key'],
        'SaveAs' => '/tmp/'.$path[1]
    ]);

    $image = imagecreatefromjpeg('/tmp/'.$path[1]);
    $watermark = imagecreatefromstring(base64_decode(''));

    $marge_right = 10;
    $marge_bottom = 10;
    $sx = imagesx($watermark);
    $sy = imagesy($watermark);

    imagecopy($image, $watermark, imagesx($image) - $sx - $marge_right, imagesy($image) - $sy - $marge_bottom, 0, 0, imagesx($watermark), imagesy($watermark));

    ob_start();
    imagejpeg($image);
    $image_contents = ob_get_clean();

    $result = $s3->putObject([
        'ContentType' => $result['ContentType'],
        'Bucket' => $data['Records'][0]['s3']['bucket']['name'],
        'Key' => "watermark/".$path[1],
        'Body' => $image_contents
    ]);

    return 'Watermark added';
}
